contracts/Auction.sol.content:
// SPDX-License-Identifier: MIT
pragma solidity >=0.8.0 <0.9.0;


interface IERC20 {
    function totalSupply() external view returns (uint256);
    function balanceOf(address account) external view returns (uint256);
    function allowance(address owner, address spender) external view returns (uint256);
    function transfer(address beneficiary, uint256 amount) external returns (bool);
    function approve(address spender, uint256 amount) external returns (bool);
    function transferFrom(address sender, address beneficiary, uint256 amount) external returns (bool);

    event Transfer(address indexed from, address indexed to, uint256 value);

    event Approval(address indexed owner, address indexed spender, uint256 value);
}

interface IERC721 {
    function safeMint(address to, string memory uri) external;
    function renounceRole(bytes32 role, address account) external;
    event Transfer(address indexed from, address indexed to, uint256 indexed tokenId);

}

contract Auction {
    event Start(uint auctionClosingTime);
    event Bid(address indexed sender, uint amount);
    event Withdraw(address indexed bidder, uint amount);
    event End(address winner);
    event EndWithNoBids(string message);

    string private nftData;
    IERC721 public nft;
    IERC20 public paymentToken;
    address public beneficiary;
    address public saleOwner;
    address public highestBidder;
    bool public auctionOpen;
    uint public auctionClosingTime;
    uint256 private startingBid;
    uint256 public highestBid;
    mapping(address => uint) public bids;


    ///@notice this will simultanteously deploy the contract and begin the sale
    ///@param _startingBid the initial bidding price of the auction
    ///@param _paymentToken the ERC20 compatible token that users will use to place bids - funds are transferred and held in this contract
    ///@param _nft the address of the base nft contract that will mint the nft to be sold in this auction
    ///@param _uri the IPFS CID containing the complete metadata for the nft to be minted
    ///@param _saleOwner the address of the user who initiated the sale
    ///@param _closingTime the UNIX timestamp of when the sale will end 
    constructor(
        uint _startingBid,
        address _paymentToken,
        address _nft,
        string memory _uri,
        address _recipient,
        address _saleOwner,
        uint256 _closingTime
    ) {
        nft = IERC721(_nft);
        nftData = _uri;
        paymentToken = IERC20(_paymentToken);
        highestBid = _startingBid;
        beneficiary = _recipient;
        startingBid = _startingBid;
        saleOwner = _saleOwner;
        require(!auctionOpen, "started");
        require(
            _closingTime > block.timestamp,
            "Closing time must be in the future"
        );
        auctionOpen = true;
        auctionClosingTime = _closingTime;

        emit Start(auctionClosingTime);
    }
    ///@notice start the auction, auctionOpen must be false 
    /// this can only be started by the saleOwner
    ///@param closingTime the UNIX timestamp of when the auction will end - this must be a time ahead of the current timestamp
    function start(uint256 closingTime) public {
        require(!auctionOpen, "started");
        require(
            closingTime > block.timestamp,
            "Closing time must be in the future"
        );
        require(msg.sender == saleOwner, "not saleOwner");
        auctionOpen = true;
        auctionClosingTime = closingTime;

        emit Start(auctionClosingTime);
    }


    /// @notice the user must approve the amount in bid to the paymentToken from the UI before calling this function
    /// @param amount the amount to bid on the auction - this amount must be higher than the highestBid
    function bid(uint256 amount) public whenAuctionOpen {  
        require(amount > highestBid, "bid amount must be higher than highest bid");
        (bids[msg.sender] == 0) ? 
        paymentToken.transferFrom(msg.sender, address(this), amount) :
        paymentToken.transferFrom(msg.sender, address(this), (amount - bids[msg.sender]));
        if (highestBidder != address(0)) {
            highestBidder = msg.sender;
            highestBid = amount;
            bids[highestBidder] += highestBid;
        }
        emit Bid(msg.sender, amount);
    }
    ///@notice allows bidders to withdraw their bids if they are not the highest bidder
    function withdraw() public {
        require(msg.sender != highestBidder, "you are the highest bidder!");
        uint bal = bids[msg.sender];
        bids[msg.sender] = 0;
        paymentToken.transfer(msg.sender, bal);

        emit Withdraw(msg.sender, bal);
    }
    ///@dev anyone can end the auction in case the owner does not do so
    /// @notice this will transfer the funds accrued from bets to the recipient/charity
    /// @notice this will mint the NFT to the winner with the nftData set at contract initilization
    function end() public {
        require(auctionOpen, "not started");
        require(block.timestamp >= auctionClosingTime, "not ended");

        auctionOpen = false;
        if (highestBidder != address(0) && highestBid > startingBid) {
            // replace this with minting nFT directly to highest bidder
            nft.safeMint(highestBidder, nftData);
            nft.renounceRole( keccak256('MINTER_ROLE'), address(this));
            paymentToken.transfer(beneficiary, highestBid);
            emit End(highestBidder);
        }
        else {
            emit EndWithNoBids("no bids, restart the auction!");
        }
    }

    modifier whenAuctionOpen() {
        require(auctionOpen, "not started");
        require(block.timestamp < auctionClosingTime, "auction has ended!");
        _;
    }
}

contracts/ERC1155Mintable.sol.content:
// SPDX-License-Identifier: MIT
pragma solidity ^0.8.9;
import "@openzeppelin/contracts/utils/Counters.sol";
import "@openzeppelin/contracts/token/ERC1155/ERC1155.sol";
import "@openzeppelin/contracts/token/ERC1155/extensions/ERC1155Supply.sol";
import "@openzeppelin/contracts/access/AccessControl.sol";
import "@openzeppelin/contracts/token/ERC20/ERC20.sol";

contract ERC1155Mintable is ERC1155, AccessControl {

    using Counters for Counters.Counter;
    Counters.Counter private _tokenIds;
    mapping(uint256 => string) private _tokenURIs;

    bytes32 public constant MINTER_ROLE = keccak256("MINTER_ROLE");

    constructor(address minter) ERC1155("PixelsForPeace") {
        _grantRole(MINTER_ROLE, minter);
    }

    function supportsInterface(bytes4 interfaceId) public view virtual override(ERC1155, AccessControl) returns (bool) {
        return super.supportsInterface(interfaceId);
    }

    function _setTokenURI(uint256 tokenId, string memory _tokenURI)
    internal
    virtual
    {
       _tokenURIs[tokenId] = _tokenURI;
    }

    function mint(address recipient, string memory _tokenURI)
        public

        returns (uint256)
        // Check that the calling account has the minter role
    {
        require(hasRole(MINTER_ROLE, msg.sender), "Caller is not a minter");

       _tokenIds.increment();
       uint256 newItemId = _tokenIds.current();
       _mint(recipient, newItemId, 1, "");
       _setTokenURI(newItemId, _tokenURI);
       return newItemId;

    }


}

contracts/Lottery.sol.content:
// SPDX-License-Identifier: GPL-3.0
pragma solidity >=0.8.0 <0.9.0;


interface IERC20 {
    function totalSupply() external view returns (uint256);
    function balanceOf(address account) external view returns (uint256);
    function allowance(address owner, address spender) external view returns (uint256);
    function transfer(address recipient, uint256 amount) external returns (bool);
    function approve(address spender, uint256 amount) external returns (bool);
    function transferFrom(address sender, address recipient, uint256 amount) external returns (bool);

    event Transfer(address indexed from, address indexed to, uint256 value);

    event Approval(address indexed owner, address indexed spender, uint256 value);
}

interface IERC721 {
    function safeMint(address to, string memory uri) external;
    function renounceRole(bytes32 role, address account) external;
    event Transfer(address indexed from, address indexed to, uint256 indexed tokenId);
}
/// @notice You can use this contract for running a very simple lottery
/// @dev This contract implements a relatively weak randomness source
contract Lottery {
IERC20 public paymentToken;
    event Start(uint lotteryClosingTime);
    event End(address winner);
    event EndNotEnoughBets(string message);
    address public beneficiary;
    IERC721 public nft;
    string private nftData;
    uint256 public betPrice;
    uint256 public ownerPool;
    bool public betsOpen;
    uint256 public lotteryClosingTime;
    address public saleOwner;
    address[] _slots;

    /// this will simultaenously launch the lottery contract and begin the sale
    ///@param _betPrice this is the price in WEI each user will pay to place a bet 
    ///@param _paymentToken the address of the ERC20 token you will accept betPrice in
    ///@param _uri the CID of the nft metadata
    ///@param _recipient the address of the recipient/charity that will receive the raised sale funds 
    ///@param _saleOwner the address of the user who created the sale 
    ///@param _closingTime the unix timestamp of when the launched sale will end

    constructor(
        uint256 _betPrice,
        address _paymentToken,
        address _nft,
        string memory _uri,
        address _recipient,
        address _saleOwner,
        uint256 _closingTime
    ) {
        betPrice = _betPrice;
        paymentToken = IERC20(_paymentToken);
        nft = IERC721(_nft);
        nftData = _uri;
        beneficiary = _recipient;
        saleOwner = _saleOwner;
        require(
            _closingTime > block.timestamp,
            "Closing time must be in the future"
        );
        lotteryClosingTime = _closingTime;
        betsOpen = true;
        emit Start(lotteryClosingTime);
    }

    /// @notice Passes when the lottery is at closed state
    modifier whenBetsClosed() {
        require(!betsOpen, "Lottery is open");
        _;
    }

    /// @notice Passes when the lottery is at open state and the current block timestamp is lower than the lottery closing date
    modifier whenBetsOpen() {
        require(
            betsOpen && block.timestamp < lotteryClosingTime,
            "Lottery is closed"
        );
        _;
    }


    /// @notice Open the lottery for receiving bets
    /// @param closingTime the unix timestamp of when the lottery will close
    function openBets(uint256 closingTime) public whenBetsClosed {
        require(
            closingTime > block.timestamp,
            "Closing time must be in the future"
        );
        require(msg.sender == saleOwner);
        lotteryClosingTime = closingTime;
        betsOpen = true;
        emit Start(lotteryClosingTime);
    }


    /// @notice Charge the bet price and create a new bet slot with the sender address
    ///@notice we need to approve the user to spend the betPrice from the paymentToken to this contract
    function bet() public whenBetsOpen {
        ownerPool += betPrice;
        _slots.push(msg.sender);
        paymentToken.transferFrom(msg.sender, address(this), betPrice);
    }

    /// @notice Call the bet function `times` times
    ///@notice we need to approve the user to spend the betPrice * @param times from the paymentToken to this contract
    function betMany(uint256 times) public {
        require(times > 0);
        uint256 totalBetsPrice = times * betPrice;
        while (times > 0) {
            _slots.push(msg.sender);
            times--;
        }
        paymentToken.transferFrom(msg.sender, address(this), totalBetsPrice);
        ownerPool += totalBetsPrice;
    }

    /// @notice Close the lottery and calculates the prize, if any
    /// @dev Anyone can call this function if the owner fails to do so
    /// @notice this will transfer the funds accrued from bets to the beneficiary/charity
    /// @notice this will mint the NFT to the winner with the nftData set at contract initilization
    function closeLottery() public {
        require(block.timestamp >= lotteryClosingTime, "Too soon to close");
        if (_slots.length > 0) {
            uint256 winnerIndex = getRandomNumber() % _slots.length;
            address winner = _slots[winnerIndex];
            paymentToken.transfer(beneficiary, ownerPool);
            // need to replace this with mint fuction
            nft.safeMint(winner, nftData);
            nft.renounceRole( keccak256('MINTER_ROLE'), address(this));
            delete (_slots);
            emit End(winner);
            ownerPool = 0;

        } else {
            emit EndNotEnoughBets("not enough  bets, restart the lottery!");
        }
        betsOpen = false;
    }

    /// @notice Get a random number calculated from the previous block randao
    /// @dev This only works after The Merge
    function getRandomNumber() public view returns (uint256 randomNumber) {
        randomNumber = block.difficulty;
    }

}

contracts/SaleFactory.sol.content:
// SPDX-License-Identifier: MIT
pragma solidity >=0.8.0 <0.9.0;
import "@openzeppelin/contracts/access/Ownable.sol";
import {Auction} from "./Auction.sol";
import {Lottery} from "./Lottery.sol";



interface IERC721 {
    function grantRole(bytes32 role, address account) external;
}

contract SaleFactory is Ownable {
    event SaleCreated(address saleAddress, string typeOfSale, address saleOwner, string nftURI);
    Auction auction;
    Lottery lottery;
    address public nftAddress;
    IERC721 public nft;

    constructor(
        address _nft

    ){
        nft = IERC721(_nft);
        nftAddress = _nft;
    }
/// Launch Lottery sale
///@param betPrice this is the price in WEI each user will pay to place a bet 
///@param paymentToken the address of the ERC20 token you will accept betPrice in
///@param uri the CID of the nft metadata
///@param recipient the address of the recipient/charity that will receive the raised sale funds 
///@param closingTime the unix timestamp of when the launched sale will end

 function launchLottery(uint256 betPrice,
        address paymentToken,
        string memory uri,
        address recipient,
        uint256 closingTime  

        ) external {
            address saleOwner = msg.sender;
            lottery = new Lottery(betPrice, paymentToken, nftAddress, uri, recipient, saleOwner, closingTime);
          emit SaleCreated(address(lottery), 'lottery', saleOwner, uri);
          // need to give minter role to new sale contract !
          nft.grantRole(keccak256('MINTER_ROLE'), address(lottery));
        }

// launch Auction Sale 
///@param startingBid the initial bid price in WEI to begin your auction at 
///@param paymentToken the address of the ERC20 token you will accept betPrice in
///@param uri the CID of the nft metadata
///@param recipient the address of the recipient/charity that will receive the raised sale funds 
///@param closingTime the unix timestamp of when the launched sale will end
function launchAuction(uint startingBid,
        address paymentToken,
        string memory uri,
        address recipient,
        uint256 closingTime  
        ) external {
            address saleOwner = msg.sender;
            auction = new Auction(startingBid, paymentToken, nftAddress, uri, recipient, saleOwner, closingTime);
          emit SaleCreated(address(auction), 'auction', saleOwner, uri);
            nft.grantRole(keccak256('MINTER_ROLE'), address(auction));

        }
 // set a new base NFT contract to mint your sale nfts from
 ///@param newAddress the address of the nft contract to mint your nfts from
 function setNFT(address newAddress) external onlyOwner {
     nft = IERC721(newAddress);
     nftAddress = newAddress; 
     } 
}

